odel = viewModel        val gso = GoogleSignInOptions.Builder()            .requestId()            .requestIdToken(getString(R.string.server_client_id))            .requestProfile()            .requestEmail()            .build()        mGoogleSignInClient = GoogleSignIn.getClient(requireContext(), gso)        val account = GoogleSignIn.getLastSignedInAccount(requireContext())        binding.user = account        viewModel.navigateToSettings.observe(viewLifecycleOwner, Observer {            it?.let {                /* findNavController().navigate(                    MoreFragmentDirections.activitySettings(),                    FragmentNavigatorExtras(binding.buttonSettings to "settings")                ) */                val intent = Intent(requireContext(), SettingsActivity::class.java)                val options = ActivityOptions.makeSceneTransitionAnimation(                    requireActivity(),                    binding.buttonSettings,                    "settings"                )                startActivity(intent, options.toBundle())                viewModel.onNavigatedToSettings()            }        })        viewModel.navigateToTeacherEvaluation.observe(viewLifecycleOwner, Observer {            it?.let {                val extras = FragmentNavigatorExtras(                    binding.buttonTeacherEvaluation to "teacherEvaluation"                )                findNavController().navigate(                    MoreFragmentDirections.actionNavigationMoreToNavigationTeacherEvaluation(),                    extras                )                viewModel.onNavigatedToTeacherEvaluation()            }        })        viewModel.signOut.observe(viewLifecycleOwner, Observer {            it?.let {                // val transform = MaterialContainerTransform().apply {  }                val builder = MaterialAlertDialogBuilder(requireContext())                    .setTitle(requireContext().getString(R.string.text_sign_out))                    .setMessage(getString(R.string.text_are_u_sure))                    .setPositiveButton(requireContext().getString(R.string.text_ok)) { dialog, _ ->                        mGoogleSignInClient.signOut().addOnCompleteListener { task ->                            if (task.isComplete) {                                dialog.dismiss()                                startActivity(Intent(requireContext(), WelcomeActivity::class.java))                                requireActivity().finish()                            }                        }                    }                    .setNegativeButton(requireContext().getString(R.string.text_cancel)) { dialog, _ ->                        dialog.dismiss()                    }                builder.create().show()                viewModel.onSignedOut()            }        })        viewModel.navigateToElearning.observe(viewLifecycleOwner, Observer {            it?.let {                val launchIntent = requireContext().packageManager.getLaunchIntentForPackage("vn.udn.vku.elearning")                launchIntent?.let { intent ->                    startActivity(intent)                }                viewModel.onNavigatedToElearning()            }        })    }}